import React from 'react';
import Axios from 'axios';
import Logo from "../img/logo.png";
import Menu from "../img/menu-alt.png";
import { Link } from 'react-router-dom';
import Logout from '../register/Logout'

export default class NavBarHome extends React.Component{

	componentDidMount = () => {

		const config = {
			headers: {
				Authorization: 'Bearer ' + localStorage.getItem('cookie'),
			}
		};

		Axios.get('http://localhost:4000/api/user/', config)
		.then(res => {
			console.log(res.data.user);
		})
		.catch(err =>{
			console.log(err);
		})
	}

	render(){
		return <div id="NavBar">
			<div>
				<Link className="Logo" to="/Home">
					<img src={Logo} width="70" heigth="70" alt="" />
				</Link>
				<Link className="Titre" to='/Home'>
					<h1>Cheeky</h1>
				</Link>
				<div className="Menu">
					<ul>
						<li className="Active">
							<a href="#">
								<img src={Menu} alt=""/>
							</a>
							<ul className="MenuCRS">
								<li className="CRS">
									<Link to='/Profil'>
										<button onClick={componentDidMount}>
											Profil
										</button>
									</Link>
								</li>
								<li className="CRS">
									<Link to='/Messages'>
										Messages
									</Link>
								</li>
								<li className="CRS">
									<Link to='/Friends'>
										Friends
									</Link>
								</li>
								<li className="CRS">
									<Logout/>
								</li>
							</ul>
						</li>
					</ul>
				</div>
			</div>
		</div>
	}
}
