import React from 'react';
import MessagesPage from './MessagesPage';
import axios from 'axios';

const api = axios.create({
  baseURL : 'http://localhost:4000/', 
  timeout : 1000,
  headers : {'Custom-Header' : 'NameHeader'}
});

export default class Login extends React.Component{

	cosntructor(props)
	{
		// creation des ref
		this.login_ref = React.createRef()
		this.password_ref = React.createRef();
		// status return by server is stored here
		this.state = {status: ''};
	}



		// traite la reponse du serveur
	response_login(response)
	{
		// verifier si erreur
		if(response.data['status'] === 200)
		{
			this.setState({status:''})
			this.props.login(response.data['id']);
		}
		// sinon renvoyer l'ID user vers fonction login de main page 
		else
		{
			this.setState({status:'error', desc: response.data['desc']})
		}
	}

	// Recuperer valeur form et envoyer
	send()
	{
		var login = login_ref.current.value;
		var password = pass_ref.current.value;
		api.post('api/user/login', {login:login, password:password})
		.then((resp) => this.response_login(resp))
		.catch(() => {alert('Erreur de com')})
	}

	setConnected = () => {
		alert("Vous êtes connectés !");
	}

	render(){

		return  <div className="LoginForm">
			<div className="Form_box">
				<div className="Button_Box">
					<div id="BTN"></div>
					<button type="button" className="Button">
						LogIn
					</button>
				</div>
				<div className="Input_login">
					<form id="LogIn" className="Input">
						<input type="text" className="Input-Field" placeholder="Login" ref={login_ref}/>
						<input type = "password" className="Input-Field" placeholder="Password" ref={password_ref}/>
						<div className="Checkbox">
							<input type="checkbox" className="Checkbox"/>
							<span>
								Remember Passeword
							</span>
						</div>
						<button className="Submit" onClick={this.setConnected}>
							Log In
						</button>
					</form>
				</div>
			</div>
		</div>
	}
}
