import React from 'react';
import MessagesPage from './MessagesPage';
import axios from 'axios';

const api = axios.create({
  baseURL : 'http://localhost:4000/', 
  timeout : 1000,
  headers : {'Custom-Header' : 'NameHeader'}
});

export default class Login extends React.Component{

	setConnected = () => {
		alert("Vous êtes connectés !");
	}

	render(){

		return  <div className="LoginForm">
			<div className="Form_box">
				<div className="Button_Box">
					<div id="BTN"></div>
					<button type="button" className="Button">
						LogIn
					</button>
				</div>
				<div className="Input_login">
					<form id="LogIn" className="Input">
						<input type="text" className="Input-Field" placeholder="Login" ref={login_ref}/>
						<input type = "password" className="Input-Field" placeholder="Password" ref={password_ref}/>
						<div className="Checkbox">
							<input type="checkbox" className="Checkbox"/>
							<span>
								Remember Passeword
							</span>
						</div>
						<button className="Submit" onClick={this.setConnected}>
							Log In
						</button>
					</form>
				</div>
			</div>
		</div>
	}
}
