import React from 'react';
import NavigationPanel from './NavigationPanel';
import MessagesPage from './MessagesPage';
import Home from './Home';
import { Register } from './Register'
import Login from './Login'

export default class MainPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: 'login', // valeurs possibles: 'login', 'messages', 'signin',
      isConnected: false,
    }
  }

  setConnected = () => {
    this.setState({
      isConnected: true,
      currentPage: 'messages',
    });
  }

  setLogout = () => {
    this.setState({
      isConnected: false,
      currentPage: 'login',
    });
  }

  signup = () => {
    this.setState({ currentPage: 'signup' });
  }

  signin = () => {
    this.setState({
      currentPage: 'signin',
    });
  }

  render() {
    const { login, logout, signup, signin, isConnected, currentPage} = this.props;

    return <div>
      <NavigationPanel
        isConnected={isConnected}
        login={() => { this.setConnected() }}  
        logout={() => { this.setLogout() }}  
        signup={() => { this.signup() }}
        signin={() => { this.signin() }}
      />
      <main>
        {!isConnected && <p>
          Pas encore de compte ?
          <button className="SignUp" onClick={() => { this.signup(); }}>S'inscrire</button>
