import React, { useEffect  } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { isEmpty } from '../Utilitaire/Util';
import Card from "./Post/Card";
import NavBarHome from "../NavBar/NavBarHome"
import {getTrends} from '../Utilitaire/actions/post.actions';


const Trending = () => {
	const trendList = useSelector((state) => state.trendingReducer);
	const dispatch = useDispatch();
	const posts  = useSelector((state) => state.allPostsReducer);

	useEffect(() => {
		//calcul le post le plus aimé
		if(!isEmpty(posts[0])){
			const postsArr = Object.keys(posts).map((i) => posts[i]); //permet de créer une liste de post du plus aimé au moins aimé
			
			let sortedArray = postsArr.sort((a, b) => {
				return b.likers.length - a.likers.length; //pour ranger la liste
			});
			
			sortedArray.length = 5; //il garde que les 3 plus aimé
			dispatch(getTrends(sortedArray));
		}
	}, [posts, dispatch])

	return (
		<div className="trending-page">
			<NavBarHome />
			<div className="main">
				<ul>
					{!isEmpty(trendList[0]) && trendList.map((post) => <Card post={post} key={post._id} />)}   
				</ul>
			</div>
		</div>
	);
};

export default Trending;
