import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Axios from 'axios';

import Page from './Page';
import NewPost from './Post/NewPost';
import NavBarHome from '../NavBar/NavBarHome';
import { UserPost } from '../Utilitaire/reducers/userActions';
import { getAllUsers } from '../Utilitaire/reducers/allusersAction';
import Post from './Post/Post';
import { isEmpty, dateParser } from '../Utilitaire/Util';
import NewCom from './Post/NewCom';
import LikeAPost from './Post/LikePost';
import DeleteAPost from './Post/DeletePost';

const Home = () => {

	const users = useSelector((state) => state.authReducer.user);
	const posts = useSelector((state) => state.userReducer.posts);
	const alluser = useSelector((state) => state.allusersReducer.payload);
	const [ showComments, setShowComments ] = useState(false);


	const dispatch = useDispatch();

	useEffect(() => {
		dispatch(getAllUsers());
	})

	const All = () => {
		console.log(alluser[0]);
	}

	return (
		<div id="HomeForm">
			<NavBarHome/>
			<div className="Post">
				<NewPost/>
			</div>
			<button onClick={All}>
				All
			</button>
			{isEmpty(alluser) 
				? (<h3> Aucun User </h3>) 
				: (
					isEmpty(posts[0]) 
						? (<h3> Aucun Post </h3>) 
						: (
							posts.map((post) => {
								alluser.map((friend) => {
									if(post.author._id === friend._id){
										return (
											<ul> 
												<li>
													<div className="Header">
														<div className="UserLogin">
															{friend.login}<br/>
														</div>
														<span>{dateParser(post.createdAt)}</span>
													</div>
													<div className="UserPost">
														{post.post}<br/>
													</div>
													<div className="footer">
														<DeleteAPost id={post._id} />
														<div className="card-footer">
															<div className="comment-icon">
																<img
																	src="./img/icons/message1.svg"
																	onClick={() => setShowComments(!showComments)}
																	alt="comment"
																	/>
																<span>{post.comments.length}</span>
															</div>
															<LikeAPost post={post}/>
														</div>
														{showComments && <NewCom post={post} />}
													</div>
												</li>
											</ul>
										)
									}
								})
							})
						))}
		</div>
	)
}

export default Home;
