import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { addPost, getPosts } from '../../Utilitaire/actions/post.actions';
import { isEmpty, timestampParser } from '../../Utilitaire/Util';

const NewPostForm = () => {
	const [isLoading, setIsLoading] = useState(true); //pour mettre un chargement pendant la charge de la data
	const [message, setMessage] = useState(""); //texte du message
	const [postPicture, setPostPicture] = useState(null); /*on peut avoir des images dans notre post --> front*/
	const [video, setVideo] = useState(''); //pareil pour les vidéos
	const [file, setFile] = useState(); //pour les images --> back
	const userData = useSelector((state) => state.userReducer); //charger le contenu de notre store (user et users)
	const error = useSelector((state) => state.errorReducer.postError);
	const dispatch = useDispatch();

	const handlePost = async () => {
		if (message || postPicture || video) {
			const data = new FormData(); //stocker le file + texte ...
			data.append('posterId', userData._id);
			data.append('message', message);
			if (file) {
				data.append("file", file);
			}
			data.append('video', video);

			await dispatch(addPost(data)); // on envoie la data pour faire la requete 
			dispatch(getPosts());
			cancelPost();

		} else {
			alert("Veuillez entrer un message");
		}
	};

	const handlePicture = (e) => {
		setPostPicture(URL.createObjectURL(e.target.files[0])); //on lui passe la photo, pour la prévisualisation de l'image
		setFile(e.target.files[0]); //permet d'envoyer l'image au front
		setVideo(''); //on peut pas avoir de vidéo si on a un deja une image
	};

	const cancelPost = () => {
		setMessage('');
		setPostPicture('');
		setVideo('');
		setFile('');
	};

	useEffect(() => {
		//si la data est vide alors on fait l'icone de chargement
		if (!isEmpty(userData)) {
			setIsLoading(false);
		}

		const handleVideo = () => {
			let findLink = message.split(" "); //on split notre message des qu'il y a un espace -> permet de detecter les liens youtubes
			for (let i = 0; i < findLink.length; i++) {
				if (findLink[i].includes('https://www.yout') || findLink[i].includes('https://yout')) { //on regarde si c'est un lien youtube
					let embed = findLink[i].replace('watch?v=', "embed/"); //dans les liens youtubes il ya "watch", on replace par embed --> permet d'être lisible dans un lecteur autre que youtube
					setVideo(embed.split('&')[0]); //on split a partir du & et on garde la partie de gauche (c'est le time code généralement )
					findLink.splice(i, 1); //permet de retirer l'url du message
					setMessage(findLink.join(" "));
					setPostPicture('');
				}
			}
		}

		handleVideo();
	}, [userData, message, video])

	return (
		<div className="post-container">
			{isLoading ? (
				<i className="fas fa-spinner fa-pulse"></i>
			) : (
				<div className='Post'>
					<div className="post-form">
						<div className="card-container">
						
							
							<div className="card-right">
								<textarea
									name="message"
									id="message"
									placeholder="Quoi de beau ?"
									onChange={(e) => setMessage(e.target.value)}
									value={message}
									rows="5"
									cols="60"
								/>
							</div>
							<div className="footer-form"> {/*Si on a mis une image alors on peut pas mettre de vidéo et inversement */}
								<div className="icon">
									{isEmpty(video) && (
										<>

											<img src="./img/icons/picture.svg" alt="img" />
											<input
												type="file"
												id="file-upload"
												name="file"
												accept=".jpg, .jpeg, .png"
												onChange={(e) => handlePicture(e)}
											/>

										</>

									)}
								</div>
							</div>
						</div>

						{postPicture || video.length > 20 ? (
							<li>
								<div className="card-container">
									<div className="card-left">
										<img src={userData.picture} alt="user-pic" />
									</div>
									<div className="card-right">
										<div className="card-header">
											<div className="pseudo">
												<h3> {userData.pseudo}</h3>
											</div>
											<span>{timestampParser(Date.now())}</span>
										</div>
										<div className="content">
											<img src={postPicture} alt="" />
											{video && (
												<iframe
													src={video}
													frameBorder="0"
													allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
													allowFullScreen
													title={video}
												></iframe>
											)}
										</div>
									</div>
								</div>
							</li>
						) : (
							null
						)}
						
						<div className="footer-form"> {/*Si on a mis une image alors on peut pas mettre de vidéo et inversement */}
							{!isEmpty(error.format) && <p>{error.format}</p>}
							{!isEmpty(error.maxSize) && <p>{error.maxSize}</p>}
							
							<div className="btn-send">
								{message || postPicture || video.length > 20 ? ( /* permet d'afficher le bouton uniquement quand il y a du contenu dedans */
									<button className="cancel" onClick={cancelPost}>Annuler message</button>
								) : (
									null
								)}

								<button className="send" onClick={handlePost}>Envoyer</button>
							</div>
						</div>
					</div>
				</div>
			)}
		</div>
	);
};

export default NewPostForm;
