import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { addPost, getPosts } from '../../Utilitaire/actions/post.actions';
import { isEmpty, timestampParser } from '../../Utilitaire/Util';

const NewPostForm = () => {
    const [isLoading, setIsLoading] = useState(true); //pour mettre un chargement pendant la charge de la data
    const [message, setMessage] = useState(""); //texte du message
    const userData = useSelector((state) => state.userReducer); //charger le contenu de notre store (user et users)
    const error = useSelector((state) => state.errorReducer.postError);
    const dispatch = useDispatch();

    const handlePost = async () => {
        if (message) {
            const data = new FormData(); //stocker le file + texte ...
            data.append('posterId', userData._id);
            data.append('message', message);

            await dispatch(addPost(data)); // on envoie la data pour faire la requete 
            dispatch(getPosts());
            cancelPost();

        } else {
            alert("Veuillez entrer un message");
        }
    };

    const cancelPost = () => {
        setMessage('');
    };

    useEffect(() => {
        //si la data est vide alors on fait l'icone de chargement
        if (!isEmpty(userData)) {
            setIsLoading(false);
        }
    }, [userData, message])

    return (
        <div className="post-container">
            {isLoading ? (
                <i className="fas fa-spinner fa-pulse"></i>
            ) : (
                <>
                    <div className="post-form">
                        <div className="card-container">

                            <div className="card-left">
                                <NavLink exact to="/profil">
                                    <div className="user-info">
                                        <img src={userData.picture} width="80px" height="80px" alt="user-img" /*quand on clique sur la photo de l'utilisateur alors on va dans notre profil */ />
                                    </div>
                                </NavLink>
                            </div>

                            <div className="card-right">
                                <textarea
                                    name="message"
                                    id="message"
                                    placeholder="Quoi de beau ?"
                                    onChange={(e) => setMessage(e.target.value)}
                                    value={message}
                                />
                            </div>
                        </div>

                        <div className="footer-form"> {/*Si on a mis une image alors on peut pas mettre de vid√©o et inversement */}

                            {!isEmpty(error.format) && <p>{error.format}</p>}
                            {!isEmpty(error.maxSize) && <p>{error.maxSize}</p>}
                            <div className="btn-send">
                                {message || postPicture || video.length > 20 ? ( /* permet d'afficher le bouton uniquement quand il y a du contenu dedans */
                                    <button className="cancel" onClick={cancelPost}>Annuler message</button>
                                ) : null}

                                <button className="send" onClick={handlePost}>Envoyer</button>
                            </div>
                        </div>
                    </div>
                </>
            )}
        </div>
    );
};

export default NewPostForm;
