import React,{useEffect,useState,useContext} from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';
import { UserContext } from '../NavigationPanel';

const Profil  = () => {
	const [ userProfile, setProfile ] = useState(null)

	const { state, dispatch } = useContext(UserContext);
	const { userid } = useParams();
	const [ showfollow, setShowFollow ] = useState( state?!state.abonne.includes(userid):true);

	useEffect(() => {
		Axios(
			`http://localhost:4000/user/${userid}`,
			{
				headers:
				{
					Authorization: "Bearer " + localStorage.getItem( "cookie" )
				}
			}
		).then(res => res.json())
		.then(result => {
			setProfile(result)
		})
	},[])


	const followUser = () => {
		Axios(
			'http://localhost:4000/friend/follow',
			{
				method:"PATCH",
				headers:
				{
					Authorization: "Bearer " + localStorage.getItem( 'cookie' )
				},
				body: {
					abonnement: userid
				}
		}).then(res => res.json())
		.then(data => {

			dispatch(
				{
					type: "UPDATE",
					payload: {
						abonne: data.abonne,
						abonnement: data.abonnement
					}
				}
			)
			localStorage.setItem("user", data);
			setProfile((prevState) => {
				return {
					...prevState,
					user: {
						...prevState.user,
						abonne:[ ...prevState.user.abonne, data._id ]
					}
				 }
			})
			setShowFollow( false );
		})
	}
	const unfollowUser = () => {
		fetch(
			'http://localhost:4000/friend/unfollow',
			{
				method:"PATCH",
				headers:
				{
					Authorization: "Bearer " + localStorage.getItem( 'cookie' )
				},
				body: {
					id: userid
				}
		}).then(res => res.json())
		.then(data => {
			dispatch(
				{
					type:"UPDATE",
					payload:{
						abonne: data.abonne,
						abonnement: data.abonnement
					}
				}
			)
			localStorage.setItem("user", data);

			setProfile((prevState) => {
				const newFollower = prevState.user.abonnement.filter(item => item !== data._id )
				return {
					...prevState,
					user: {
						...prevState.user,
						abonnement: newFollower
					}
				}
			})
			setShowFollow( true );
		})
	};

	return (
		<>
			{userProfile ?
				<div style={{maxWidth:"550px",margin:"0px auto"}}>
					<div style={{
						display:"flex",
						justifyContent:"space-around",
						margin:"18px 0px",
						borderBottom:"1px solid grey"
						}}>

						<div>
							<h4>{userProfile.user.firstname}</h4>
							<h4>{userProfile.user.lastname}</h4>
							<h5>{userProfile.user.email}</h5>

							<div style={{display:"flex",justifyContent:"space-between",width:"108%"}}>
								<h6>{userProfile.posts.length} posts</h6>
								<h6>{userProfile.user.abonne.length} Abonn√©</h6>
								<h6>{userProfile.user.abonnement.length} Abonnement</h6>
							</div>

							{showfollow?
								<button style={{
										margin:"10px"
								 	}} className="btn waves-effect waves-light #64b5f6 blue darken-1"
									onClick={()=>followUser()}
									>
									Follow
								</button>
							: 
								<button
									style={{
										margin:"10px"
									}}
									className="btn waves-effect waves-light #64b5f6 blue darken-1"
									onClick={()=>unfollowUser()}
									>
									UnFollow
								</button>
							}
						</div>
				 	</div>
				</div>
		 	: <h2>loading...!</h2>}
		</>
	)
}


export default Profil
