import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Axios from 'axios';

import Page from './Page';
import NewPost from './Post/NewPost';
import NavBarHome from '../NavBar/NavBarHome';

import NewCom from './Post/NewCom';
import LikeAPost from './Post/LikePost';
import Follow from'./Friends/Follow';
import DeleteAPost from './Post/DeletePost';

import { isEmpty, dateParser } from '../Utilitaire/Util';
import { AllPost, UpdatePost } from '../Utilitaire/reducers/userActions';

const Home = () => {

	const user = useSelector((state) => state.authReducer.user);
	const posts = useSelector((state) => state.userReducer.posts);

	const dispatch = useDispatch();

	const alluser = useSelector((state) => state.allusersReducer);

	const [ isLoading, setIsLoading ] = useState(true);
	const [ isUpdated, setIsUpdated ] = useState(false);
	const [ post, setPost ] = useState(null);
	const [ showComments, setShowComments ] = useState(false);

	const handleUpdate = async () => {
		if(post) {
			await dispatch(UpdatePost(post)).then(() => {
				Axios({
					method: "GET",
					url: "http://localhost:4000/api/post/",
					headers: {
						Authorization: "Bearer "+localStorage.getItem("cookie")
					}
				});
			})
		}
		setIsUpdated(false);
	};

	useEffect(() => {
		!isEmpty(alluser[0]) && setIsLoading(false);
	}, [alluser]);

	return (
		<div id="HomeForm">
			<NavBarHome/>
			<div className="Post">
				<NewPost/>
			</div>
			<button className="btn" onClick={AllPost}>
				Posts
			</button>
			{posts[0]._id}
			{posts[0].post}
			<ul>
				{!isEmpty(posts[0]) &&
					posts.map((post) => {
						return (
							<li className="card-container" key={post._id}>
								{isLoading ? (
									<i className="fas fa-spinner fa-spin"></i>
								) : (
									<>
										<div className="card-right">
											<div className="card-header">
												<div className="pseudo">
													<h3>
														{!isEmpty(alluser[0]) &&
														alluser.map((user) => {
															if (user._id === post.author) return user.login;
														})}
													</h3>
													{post.author !== user._id && (
														<Follow userid={post.author} type={"card"} />
													)}
												</div>
												<span>{dateParser(post.createdAt)}</span>
											</div>
											{isUpdated === false && <p>{post.post}</p>}
											{isUpdated && (
												<div className="update-post">
													<textarea
														defaultValue={post.post}
														onChange={(e) => setPost(e.target.value)}
														/>
													<div className="button-container">
														<button className="btn" onClick={handleUpdate}>
															Valider modification
														</button>
													</div>
												</div>
											)}
											{user._id === post.author && (
												<div className="button-container">
													<div onClick={() => setIsUpdated(true)}>
														{" "}
														<img src="./img/icons/edit.svg" alt="edit-comment" />
													</div>
													<DeleteAPost id={post._id} />
												</div>
											)}
											<div className="card-footer">
												<div className="comment-icon">
													<img
														src="./img/icons/message1.svg"
														onClick={() => setShowComments(!showComments)}
														alt="comment"
														/>
													<span>{post.comments.length}</span>
												</div>
												<LikeAPost post={post} />
												<img src="./img/icons/share.svg" alt="share" />
											</div>
											{showComments && <NewCom post={post} />}
										</div>
									</>
								)}
							</li>
						)
					})
				}
			</ul>
		</div>
	)
}

export default Home;
