import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import NavBarHome from '../../NavBar/NavBarHome';
import { UserPost } from '../../Utilitaire/reducers/userActions';
import { CreatePost } from '../../Utilitaire/reducers/userActions';

const Post = () => {

	const user = useSelector((state) => state.authReducer.user);
	const poster = useSelector((state) => state.userReducer.post);

	const history = useHistory();
	const dispatch = useDispatch();
	const [ post, setPost ] = useState("");

	if(!user) {
		return <h1> non connecte</h1>
	};

	const handleUpdate = () => {
		dispatch(CreatePost(post));
	};

	const handlePost = () => {
		dispatch(UserPost());
		console.log(poster);
	}

	return (
		<div id="HomeForm">
			<NavBarHome/>
			<div className="Post">
				<h4>
					Comment allez-vous aujourd'hui {user.login}?
				</h4>
				<textarea 
					type="text"
					onChange={(e) => setPost(e.target.value)}> 
				</textarea>
				<button onClick={handleUpdate}>
					Poster
				</button>

				<button onClick={handlePost}>
					My Posts
				</button>
			</div>
		</div>
	)
}

export default Post;
