import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Axios from 'axios';

import Page from './Page';
import NewPost from './Post/NewPost';
import NavBarHome from '../NavBar/NavBarHome';
import { UserPost } from '../Utilitaire/reducers/userActions';
import { isEmpty } from '../Utilitaire/Util';
import Post from './Post/Post';

const Home = () => {

	const users = useSelector((state) => state.authReducer.user);
	const posts = useSelector((state) => state.userReducer.posts);
	const alluser = useSelector((state) => state.allusersReducer);


	const dispatch = useDispatch();

	Axios({
		method: "GET",
		url: "http://localhost:4000/api/post/userspost",
		headers: {
			Authorization: "Bearer "+localStorage.getItem("cookie")
		}
	});

	const Posts = () => {
		console.log(posts);
	}

	return (
		<div id="HomeForm">
			<NavBarHome/>
			<div className="Post">
				<NewPost/>
			</div>
			<button>
				posts
			</button>
			{isEmpty(posts[0]) && posts.map((post) => {
				return (
					<ul> 
						<li>
							{users.login}<br/>
							{post.post}
						</li>
					</ul>
				)
			})}
		</div>
	)
}

export default Home;
