import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import Post from './Post/Post';
import NavBarProfil from '../NavBar/NavBarProfil';
import { updateBio } from '../Utilitaire/reducers/userActions';
import { UserPost } from '../Utilitaire/reducers/userActions';
import { isEmpty } from '../Utilitaire/Util';

const Profil = () => {

	const user = useSelector((state) => state.authReducer.user);
	const posts = useSelector((state) => state.authReducer);

	const [ bio, setBio ] = useState('');
	const [ updateForm, setUpdateForm ] = useState(false);

	const history = useHistory();
	const dispatch = useDispatch();

	const handleUpdate = () => {
		dispatch(updateBio(bio));
		setUpdateForm(false);
	}

	const Delete = () => {
		history.push('/Delete');
	}

	const [ loadPost, setLoadPost ] = useState(true);
	const [ count, setCount ] = useState(5);

	const Load = () => {
		if(
			window.innerHeight + document.documentElement.scrollTop + 1 >
			document.scrollingElement.scrollHeight
		)
		setLoadPost(true);
	};

	useEffect(() => {
		if(loadPost) {
			dispatch(UserPost(count));
			setLoadPost(false);
			setCount(count + 5);
		}

		window.addEventListener("scroll", Load);

		return () => {
			 window.removeEventListener("scroll", Load);
		}
	}, [loadPost])

	return (
		<div id="ProfilForm">
			<NavBarProfil/>
			<div className="Profil">
				<h2> Welcome {user.firstname} {user.lastname}</h2>
			</div>
			<div className="UpdateBio">
				<h3>Bio</h3>
				<div className="textarea">
					{updateForm === false && (
						<>
							<p onClick={() => setUpdateForm(!updateForm)} >{user.bio}</p>
							<button onClick={() => setUpdateForm(!updateForm)}>Modifier</button>
						</>
					)}
					{updateForm && (
						<>
							<textarea 
								type="text" 
								defaultValue={user.bio} 
								onChange={(e) => setBio(e.target.value)}>
							</textarea>
							<button onClick ={handleUpdate}>Ok ?</button>
						</>
					)}
				</div>
			</div>
			<div className="MyPost">
				<ul>
				{!isEmpty(posts[0]) &&
					posts.map((post) => {
						return <Post post={post} key={post._id}/>
					})
				}
			</ul>
			</div>
			<div className="Delete">
				Supprimer son compte 
				<button onClick={Delete}>
					Supprimer ?
				</button>
			</div>
		</div>
	)
}

export default Profil;
