import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Axios from 'axios';

import Page from './Page';
import NewPost from './Post/NewPost';
import NavBarHome from '../NavBar/NavBarHome';
import { AllPost } from '../Utilitaire/reducers/userActions';
import { isEmpty } from '../Utilitaire/Util';
import Post from './Post/Post';

const Home = () => {

	const user = useSelector((state) => state.authReducer.user);
	const posts = useSelector((state) => state.userReducer.posts);

	const dispatch = useDispatch();



	const AllPost = () => {
		Axios({
			method: "GET",
			url: "http://localhost:4000/api/post/",
			headers: {
				Authorization: "Bearer "+localStorage.getItem("cookie")
			}
		});
		console.log(posts);
	}

	return (
		<div id="HomeForm">
			<NavBarHome/>
			<div className="Post">
				<NewPost/>
			</div>
			<button className="btn" onClick={AllPost}>
				Posts
			</button>
			{user._id}
			{posts[0].post}
			<ul>
				{!isEmpty(posts[0]) &&
					posts.map((post) => {
						return (
							<ul> 
								<li>
									{post.post}<br/>
									{post.author._id}
								</li>
							</ul>
						)
					})
				}
			</ul>
		</div>
	)
}

export default Home;
