import React from 'react';
import axios from 'axios';

export default function Login() {

	const [email, setEmail] = useState('')
	const [password, setPassword] = useState('');

	const handleLogin = (e) => {
		e.preventDefault(); {/* Permet de pas recharger la page quand on clique sur Submit*/ }

			const emailError = document.querySelector(".email.error");
			const passwordError = document.querySelector(".password.error");

			axios({
				method: "post",
				url: `http://localhost:4000/api/user/login`, //ca correspond à la variable d'environnement, ca permet de pas tout réecrire si on change d'host
				withCredentials: true,
				data: {
					email,
					password,
				},
			})
			.then((res) => {
			console.log(res);

			if (res.data.errors) {
				emailError.innerHTML = res.data.errors.email;
				passwordError.innerHTML = res.data.errors.password;
			} 
			else {
				window.location = "/"; // on va présenter notre token est on va être considéré comme connecté
			}
		})
		.catch((err) => {
		console.log(err);
		});
	};

	return  (
		<div className="LoginForm">
			<div className="Form_box">
				<div className="Button_Box">
					<div id="BTN"></div>
					<button type="button" className="Button">
						LogIn
					</button>
				</div>
				<div className="Input_login">
					<form id="LogIn" className="Input" action="" onSubmit={handleLogin}>
						<input type="text" className="Input-Field" placeholder="Login"/>
						<input type = "password" className="Input-Field" placeholder="Password"/>
						<div className="Checkbox">
							<input type="checkbox" className="Checkbox"/>
							<span>
								Remember Passeword
							</span>
						</div>
						<input type="submit" className="Submit" value="Log In"/>
					</form>
				</div>
			</div>
		</div>
	)
}
