export const getAllUsers = () => {
	return(dispatch) => { //dispatch c'est ce qu'on va envoyer au reducer
		return Axios.get(
			'http://localhost:4000/api/user/'
		)
		.then((res) => {
			dispatch(
				{ 
					type: "All users", 
					payload: res.data 
				}); //toute la data qu'on vient de chercher, on la passe au reduxer
		})
		.catch((err) => console.log( err ));
	};
};

export const getUser = () => async dispatch => {
	try { 
		const config = {
			headers: {
				Authorization: 'Bearer ' + localStorage.getItem('cookie')
			}
		};

		const res = await Axios.get('http://localhost:4000/api/user/', config)
		dispatch({ 
			type: "User", 
			payload: res.data 
		}); 
	}catch(err) {
		console.log( err )
	};
};


export const updateBio = (bio) => async dispatch =>{
	try { 
		const config = {
			headers: {
				Authorization: 'Bearer ' + localStorage.getItem('cookie')
			}
		};

		const res = await Axios.put(
			'http://localhost:4000/api/user/update',
			config,
			bio
		)
		dispatch({ 
			type: "Update bio", 
			payload: res.data
		}); 
	}catch(err) {
		console.log( err )
	};
};

