import React,{useEffect,useState,useContext} from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';
import { UserContext } from '../NavigationPanel';

const Profil  = () => {
		const [userProfile,setProfile] = useState(null)

		const { state, dispatch } = useContext(UserContext);
		const { id } = useParams();
		const [ showfollow, setShowFollow ] = useState( state?!state.abonne.includes(id):true);

		useEffect(() => {
			Axios(
				`http://localhost:4000/user/${id}`,
				{
					headers:
					{
						Authorization: "Bearer " + localStorage.getItem( "cookie" )
					}
				}
			).then(res => res.json())
			.then(result => {
				setProfile(result)
			})
		},[])


		const followUser = () => {
			Axios(
				'http://localhost:4000/friend/follow',
				{
					method:"PATCH",
					headers:
					{
						Authorization: "Bearer " + localStorage.getItem( 'cookie' )
					},
					body: {
						abonnement: id
					}
			}).then(res => res.json())
			.then(data => {

				dispatch(
					{
						type: "UPDATE",
						payload: {
							abonne: data.abonne,
							abonnement: data.abonnement
						}
					}
				)
				localStorage.setItem("user", data);
				setProfile((prevState) => {
					return {
						...prevState,
						user: {
							...prevState.user,
							abonne:[ ...prevState.user.abonne, data._id ]
						}
					 }
				})
				setShowFollow( false );
			})
		}
		const unfollowUser = () => {
			fetch(
				'http://localhost:4000/friend/unfollow',
				{
					method:"PATCH",
					headers:
					{
						Authorization: "Bearer " + localStorage.getItem( 'cookie' )
					},
					body: {
						id: id
					}
			}).then(res => res.json())
			.then(data => {
				dispatch(
					{
						type:"UPDATE",
						payload:{
							abonne: data.abonne,
							abonnement: data.abonnement
						}
					}
				)
				localStorage.setItem("user", data);

				 setProfile((prevState)=>{
						const newFollower = prevState.user.followers.filter(item=>item !== data._id )
						 return {
								 ...prevState,
								 user:{
										 ...prevState.user,
										 followers:newFollower
										}
						 }
				 })
				 setShowFollow(true)

			})
		}
	 return (
			 <>
			 {userProfile ?
			 <div style={{maxWidth:"550px",margin:"0px auto"}}>
					 <div style={{
							 display:"flex",
							 justifyContent:"space-around",
							 margin:"18px 0px",
							 borderBottom:"1px solid grey"
					 }}>
							 <div>
									 <img style={{width:"160px",height:"160px",borderRadius:"80px"}}
									 src={userProfile.user.pic}
									 />
							 </div>
							 <div>
									 <h4>{userProfile.user.name}</h4>
									 <h5>{userProfile.user.email}</h5>
									 <div style={{display:"flex",justifyContent:"space-between",width:"108%"}}>
											 <h6>{userProfile.posts.length} posts</h6>
											 <h6>{userProfile.user.followers.length} followers</h6>
											 <h6>{userProfile.user.following.length} following</h6>
									 </div>
									 {showfollow?
									 <button style={{
											 margin:"10px"
									 }} className="btn waves-effect waves-light #64b5f6 blue darken-1"
										onClick={()=>followUser()}
										>
												Follow
										</button>
										: 
										<button
										style={{
												margin:"10px"
										}}
										className="btn waves-effect waves-light #64b5f6 blue darken-1"
										onClick={()=>unfollowUser()}
										>
												UnFollow
										</button>
										}



							 </div>
					 </div>

					 <div className="gallery">
							 {
									 userProfile.posts.map(item=>{
											 return(
												<img key={item._id} className="item" src={item.photo} alt={item.title}/>  
											 )
									 })
							 }


					 </div>
			 </div>


			 : <h2>loading...!</h2>}

			 </>
	 )
}


export default Profile
