import { ALLUSER } from "./allusersAction";

const initialState = {
	cookie: localStorage.getItem("cookie"),
	auth: false,
	loading: false,
};

export default function allusersReducer(state = initialState, { type, payload }) {
	switch (type) {
		case ALLUSER:
			return {
				...state,
				loading: false,
				auth: true,
				...payload,
			};
		default:
			return state;
	}
}
