import React, { useState, useContext, useEffect } from 'react';
import Axios from 'axios';
import { dateParser } from "../Utilitaire/Util";
import { UserContext } from '../NavigatonPanel';
import NavBarHome from '../NavBar/NavBarHome';

export default function Profil() {

	const { state, dispatch } = useContext(UserContext);

	useEffect(() => {
		Axios(
			'http://localhost:4000/api/post/userspost', 
			{
				method: 'GET',
				headers: {
					Authorization: "Bearer " + localStorage.getItem("cookie")
				}
			}
		).then(res => {
			console.log(res);
		}
		).catch(err => {
			console.log(err);
		});
	})

	return (
		<div id="ProfilForm">
			<NavBarHome/>
			<h2>Profil</h2>

			<div style={{maxWidth:"550px",margin:"0px auto"}}>
				<div style={{
					margin:"18px 0px",
					borderBottom:"1px solid grey"
				}}>


					<div style={{
						display:"flex",
						justifyContent:"space-around",
					}}>

						<div>
							<h4>{state?state.login:"loading"}</h4>
							<h5>{state?state.email:"loading"}</h5>

							<div style={{display:"flex",justifyContent:"space-between",width:"108%"}}>
								<h6>{state?state.abonne.length:"0"} followers</h6>
								<h6>{state?state.abonnement.length:"0"} following</h6>
							</div>

						</div>
					</div>

					<div className="file-field input-field" style={{margin:"10px"}}>
						<div className="file-path-wrapper">
							<input className="file-path validate" type="text" />
						</div>
					</div>
				</div> 
			</div>
		</div>
	)
}
