import React from 'react';
import Axios from 'axios';
import NavBarHome from '../NavBar/NavBarHome';

export default function Home() {

	state = {};

	const componentDidMount = () => {

		const config = {
			headers: {
				Authorization: 'Bearer ' + localStorage.getItem('user')
			}
		};

		Axios.get('http://localhost:4000/api/user/', config)
		.then(res => {
			console.log(res);
			this.setState({
				user: res.data
			})
		})
		.catch(err =>{
			console.log(err);
		})
	}

	return (
		<div id="HomeForm">
			<NavBarHome/>
			<div className="Post">
				<h4>
					Comment allez-vous aujourd'hui ?
				</h4>
				<textarea type="text"> 
				</textarea>
				<button>
					Poster
				</button>
			</div>
		</div>
	)
}
