import React, { useEffect, createContext, useReducer, useContext } from 'react';
import { Route, Switch, useHistory } from 'react-router-dom';
import { reducer, initialState } from './Utilitaire/userReducer';
import Axios from 'axios';

import MessagesPage from './MessagesPage';
import Footer from './NavBar/Footer'

import Acceuil from './Page/Acceuil';
import Contact from './Page/Contact';
import Friends from './Page/Friends';
import Home from './Page/Home';
import Profil from './Page/Profil';

import Logout from './register/Logout';
import SignIn from './register/SignIn';
import SignUp from './register/SignUp';

export const UserContext = createContext();

const Routing = () => {
	const history = useHistory();
	const { state, dispatch } = useContext(UserContext);

	useEffect(() => {
		const config = {
			headers: {
				Authorization: 'Bearer ' + localStorage.getItem('cookie')
			}
		};

		Axios.get('http://localhost:4000/api/user/', config)
		.then(res => {
			console.log(res);
		})
		.catch(err =>{
			console.log(err);
		})
	},[]);

	return (
		<Switch>
			<Route exact path='/'> <Acceuil/> </Route>
			<Route exact path='/Home'> <Home/> </Route>
			<Route exact path='/SignIn'> <SignIn/> </Route>
			<Route exact path='/SignUp'> <SignUp/> </Route>
			<Route exact path='/Logout'> <Logout/> </Route>
			<Route exact path='/Contact'> <Contact/> </Route>
			<Route exact path='/Messages'> <MessagesPage/> </Route>
			<Route exact path='/Profil'> <Profil/> </Route>
			<Route exact path='/Friends'> <Friends/> </Route>
		</Switch>
	)
}

export default function NavigationPanel() {
	const [ state, dispatch ] = useReducer(reducer, initialState);

	return (
		<UserContext.Provider value= {{ state, dispatch }}>
			<Routing/>
			<Footer/>
		</UserContext.Provider>
	)
}
