import { ALLUSER } from "./allusersAction";

const initialState = {
	users: {}
};

const allusersReducer = (state = initialState, { type, payload }) => {
	switch (type) {
		case ALLUSER:
			return {
				...state,
				users: payload,
			};
		default:
			return state;
	}
};

export default allusersReducer;
