import React from 'react';
import ReactDOM from 'react-dom';
import {Provider} from 'react-redux';
import { applyMiddleware, createStore } from 'redux';

import App from './App';
import reducer from './component/Utilitaire/userReducer';
import { getAllUsers } from './component/Utilitaire/userReducer';

//Ce qui aide pour faire du Redux, création de notre store
import { composeWithDevTools} from 'redux-devtools-extension';
import logger from'redux-logger';

import thunk from 'redux-thunk'; //middleware qui permet de faire des requetes asynchrones avec redux

const store = createStore(
 	reducer, composeWithDevTools(applyMiddleware(thunk))
);

ReactDOM.render(
	<Provider store = {store}>
		<App />
	</Provider>,
	document.getElementById('root')
);
